INSERT INTO `tipe_treatment` (`id_tipe_treatment`, `kode_treatment`, `nama_tipe`, `harga`, `deskripsi`) VALUES
(1, 'FT-1', 'Strawberry Exfoliating Facial', 115000, 'Strawberry Exfoliating Facial Perawatan wajah yang diracik dari buah strawberry segar. Buah strawberry memiliki kandungan antioksidan yang sangat tinggi dibanding kelompok berry lainnya dan bermanfaat mencerahkan, mengurangi noda bekas jerawat dan membantu mengecilkan pori di wajah. Cocok untuk kulit normal, dan kulit kombinasi'),
(2, 'FT-1', 'Blueberry Antioxidant Facial', 115000, 'Blueberry Antioxidant Facial Perawatan wajah yang diracik dari buah Blueberry segar yang kaya akan antioksidan dan bermanfaat mencerahkan, memperbaiki masalah kulit akibat sinar matahari, dan menjaga kestabilan kolagen di kulit wajah. Cocok untuk kulit normal, dan kulit kombinasi'),
(3, 'FT-1', 'Apple Purifying Facial', 115000, 'Apple Purifying Facial Perawatan wajah yang diracik dari buah Apel segar. Apel mengandung Alpha Hydroxy Acid (AHA) dan kaya akan Magnesium, Vitamin A, B, C. Dan membantu meremajan kulit sehingga terlihat awet muda. Cocok untuk kulit normal dan kulit kombinasi.'),
(4, 'FT-1', 'Peach Brightening Facial', 115000, 'Peach Brightening Facial Perawatan wajah yang diracik dari buah Persik segar (peach) yang kaya akan vitamin C dan membantu mencerahkan serta mengurangi noda hitam bekas jerawat. Cocok untuk kulit berminyak.'),
(5, 'FT-1', 'Orange Revitalizing Facial', 115000, 'Orange Revitalizing Facial Perawatan wajah yang diracik dari buah Jeruk segar, kaya vitamin C, membantu mencerahkan, mengontrol kelebihan minyak dan membantu mengecilkan pori di wajah. Cocok untuk kulit berminyak'),
(6, 'FT-1', 'Carrot-Papaya Rejuvenating Facial', 115000, 'Carrot-Papaya Rejuvenating Facial Perawatan wajah yang diracik dari buah Wortel dan Pepaya asli, kaya vitamin A yang membantu meremajakan, menjaga kekenyalan dan elastisitas kulit, meregenerasi sel kulit mati sehingga kulit menjadi lebih lembut. Cocok untuk kulit normal, kering dan berminyak.'),
(7, 'FT-1', 'Avocado Deep Moisturizing Facial', 115000, 'Avocado Deep Moisturizing Facial Perawatan wajah yang diformulasikan dari buah Alpukat asli, kaya akan lemak alami, asam amino, dan omega 3 yang membantu melembapkan kulit kering, menjaga kekenyalan dan elastisitas kulit, serta mencegah penuaan dini. Diperkaya dengan vitamin A, B, C, dan E serta mineral penting seperti kalium, fosfor, dan zat besi yang menutrisi dan menyehatkan kulit. Membantu menghaluskan, melembutkan, dan melindungi kulit dari radikal bebas. Cocok untuk kulit normal dan kering.'),
(8, 'FT-1', 'Banana Nourishing Facial', 115000, 'Banana Nourishing Facial Perawatan wajah yang diformulasikan dari buah Pisang asli, kaya akan vitamin A, C, E, dan B kompleks serta mineral seperti potassium, kalsium, dan fosfor yang membantu menjaga kelembapan kulit, meremajakan, serta mencegah penuaan dini dan keriput. Kandungan moisturizernya yang tinggi membantu menghaluskan, melembutkan, serta mempercepat penyembuhan kulit kering dan rusak. Cocok untuk kulit normal dan kering.'),
(9, 'FT-1', 'Yam Bean Whitening Facial', 115000, 'Yam Bean Whitening Facial Perawatan wajah yang diracik dari bengkoang asli, dikenal sebagai cooling fruit yang membantu mencerahkan kulit kusam secara alami, memutihkan, dan mengatasi warna kulit yang tidak merata. Diperkaya dengan antiseptik alami untuk meredakan gatal dan iritasi, serta kandungan alami yang membantu mengangkat sel kulit mati dan menghilangkan noda. Menyegarkan kulit setelah aktivitas seharian di bawah cuaca panas. Cocok untuk semua jenis kulit.'),
(10, 'FT-2', 'Super Blue Facial', 185000, 'Super Blue Facial merupakan perawatan wajah dengan kombinasi high frequency ozone terbaru dengan ultrasound dan masker super blue. Treatment ini sangat bagus untuk mencegah, mengurangi serta menyembuhkan jerawat. Disamping itu perawatan ini juga sangat baik membantu dalam proses pengurangan minyak yang berlebihan. Sesuai dengan kulit berjerawat dan berminyak.'),
(11, 'FT-2', 'Green Tea Facial', 120000, 'Green Tea Facial merupakan perawatan wajah dengan rangkaian cleanser, scrub, massage cream, dan masker yang mengandung ekstrak green tea. Treatment ini sangat efektif untuk mencegah, mengurangi, hingga membantu menyembuhkan jerawat. Kandungan anti bakteri dan anti oksidan yang tinggi bekerja membunuh kuman penyebab jerawat, menetralkan radikal bebas, serta membantu mengurangi timbunan minyak pada kulit. Perawatan ini juga membantu meremajakan kulit agar tampak lebih segar dan sehat. Sesuai untuk kulit normal, berminyak, dan berjerawat.'),
(12, 'FT-2', 'Bio Acne Light Therapy', 160000, 'Bio Acne Light Therapy merupakan terapi dengan Blue Light untuk menyembuhkan jerawat dan mengontrol kelebihan minyak. Jerawat yang meradang terjadi karena adanya sumbatan pada pori kulit akibat minyak yang berlebih sehingga memberi peluang P. Acne, bakteri penyebab jerawat, untuk berkembang biak. Blue Light bekerja dengan cara mengontrol dan mengurangi P. Acne, untuk mengurangi produksi minyak yang berlebih sehingga tidak menyumbat pori. Jerawat dapat mengering hanya dalam sekali terapi.'),
(13, 'FT-2', 'Bio Inflamed Acne Light Therapy', 160000, 'Bio-inflamed light therapy adalah terapi dengan Purple Light untuk menyembuhkan jerawat sekaligus mengurangi inflamasi/kemerahan akibat jerawat. Purple Light Therapy menggabungkan Blue Light dan Red Light. Blue light efektif utnuk membunuh kuman P. Acne, bakteri penyebab jerawat, dan mengontrol produksi minyak yang berlebih. Red Light efektif untuk mengurangi kemerahan atau inflamasi akibat jerawat, sehingga mempercepat penyembuhan.'),
(14, 'FT-2', 'Mesotherapy Acne Control', 300000, 'Mesotherapy Acne Control merupakan perawatan wajah dengan teknik mesotherapy non-invasif yang diformulasikan khusus untuk mengatasi jerawat membandel. Mengandung bahan aktif yang bekerja langsung ke lapisan kulit untuk mengurangi peradangan, membunuh bakteri penyebab jerawat, serta mengontrol produksi minyak berlebih. Membantu mempercepat proses penyembuhan kulit dan mencegah munculnya jerawat baru. Sangat cocok untuk kulit berminyak dan berjerawat.'),
(15, 'FT-2', 'IPL Acne', 435000, 'IPL Acne merupakan perawatan wajah dengan teknologi Intense Pulsed Light (IPL) yang efektif untuk menyembuhkan dan mengurangi berbagai jenis jerawat seperti jerawat batu, jerawat bernanah, jerawat meradang, dan jerawat bengkak. Terapi ini bekerja dengan menembakkan cahaya intens ke dalam lapisan kulit untuk membunuh bakteri penyebab jerawat, mengurangi peradangan, serta mengontrol produksi minyak berlebih. Selain itu, IPL membantu mempercepat regenerasi kulit dan meminimalkan bekas jerawat, sehingga kulit tampak lebih bersih, sehat, dan halus. Cocok untuk kulit berjerawat yang membutuhkan penanganan intensif tanpa rasa sakit.'),
(16, 'FT-3', 'Collagen Facial', 180000, 'Collagen Facial merupakan perawatan muka dengan kombinasi mikrodermabrasi ringan, infusi serum kolagen dan masker kolagen. Dengan bantuan ultrasound molekul kolagen dapat terpenetrasi ke dalam kulit untuk mengembalikan keelastisan dan kekencengan kulit, menaikkan kelembaban kulit, dan menjaga keutuhan kolagen alami kulit. Sesuai untuk kulit normal, kering, dan kusam.'),
(17, 'FT-3', 'Honey Facial', 120000, 'Honey Facial merupakan perawatan wajah dengan menggunakan cleanser, scrub, massage cream dan masker dari ramuan dengan madu asli. Perawatan ini sesuai untuk kulit kering, normal dan sensitif. Madu selain mengandung Anti Oxidant yang berasal dari beberapa vitamin dan mineral yang terkandung di dalamanya dapt mencegah penuaan dini, madu juga mengandung Anti Bacteria atau yang disebut dengan Antibiotik yang berfungsi untuk membunuh kuman, menetralkan dan menyembuhkan iritasi/infeksi, mencegah dan menyembuhkan jerawat, serta mengandung zat Progenetic Stonulous yang membantu meningkatkan keaktifan jaringan kulit.'),
(18, 'FT-3', 'Dark Chocolate Facial', 120000, 'Dark Chocolate Facial merupaakan perawatan wajah dengan cleanser, scrub, massage cream, dan masker dari coklat asli. Sesuai untuk kulit kering, normal, dan berminyak tanpa jerawat. Cocoa Butter merupakan unsur atau kandungan terkuat yang terdapat dalam coklat yang berfungsi melapisi dan melindungi kulit dari kotoran, polusi, sinar matahari, menyerap kotoran yang menempel pada kulit serta melembutkan dan menghaluskan kulit. Kemudian, selain memiliki kandungan Antioxidant yang cukup tinggi yang disebut dengan ORAC (Oxygen Radical Absorption Capacity) yang berperan sebagai penghambat terjadinya oksidasi oleh udara bebas, coklat juga kaya akan kandungan seperti lemak jenuh dan tak jenuh, Asam Amino, Protein, Karbohidrat, Kalium, Magnesium, serta berbagai macam vitamin A, B, B2, B6, B12, C dan E yang sangat baik untuk kesehatan kulit.'),
(19, 'FT-3', 'Bio Rejuvenation Light Therapy', 160000, 'Bio Rejuvenation Light Therapy adalah therapy dengan Red Light untuk merejuvinasi kulit, mengurangi keriput dan garis halus, serta mengurangi inflamasi. Red Light dapat terpenetrasi dalam samapai ke lapisan dermis kulit, dimana terdapat kolagen dan elastin. Sinar yang terpenetrasi ke dalam kulit akan diserap dan mengakibatkan efek rejuvenasi: membantu regenerasi sel, melancarkan peredaran darah, dan menstimuli produksi kolagen dan elastin.'),
(20, 'FT-3', 'Pulsed Oxygen Injection', 250000, 'Pulsed Oxygen Injection adalah perawatan wajah dengan infusi oksigen murni, serum vitamin C, dan vacuum massage. Bermanfaat untuk menyegarkan dan menyehatkan kulit, serta mencegah timbulnya jerawat. Sesai untuk kulit kering, normal, dan berminyak tanpa jerawat.'),
(21, 'FT-3', 'Mesotherapy Rejuvenation Facial', 300000, 'Mesotherapy Rejuvenation Facial adalah perawatan wajah dengan teknik mesotherapy non-invasif yang diformulasikan untuk meremajakan kulit secara menyeluruh. Mengandung kombinasi vitamin, antioksidan, dan bahan aktif yang diserap langsung ke lapisan kulit untuk meningkatkan kelembapan, mencerahkan kulit kusam, menyamarkan garis halus, dan memperbaiki tekstur kulit. Perawatan ini membantu menstimulasi regenerasi sel kulit baru sehingga kulit tampak lebih segar, kenyal, dan bercahaya alami. Cocok untuk semua jenis kulit yang ingin tampak lebih muda dan sehat.'),
(22, 'FT-3', 'Jet Peel Facial', 300000, 'Jet Peel Facial adalah terapi peremajaan dengan aliran oksigen bertekanan tinggi untuk mengangkat sel kulit mati, memberi nutrisi, melembabkan kulit, dan mencegah timbulnya jerawat. Sesuai untuk semua jenis kulit, juga aman untuk kulit sensitif sekalipun.'),
(23, 'FT-4', 'Bio Acne Light Therapy', 160000, 'Bio Acne Light Therapy merupakan terapi dengan Blue Light untuk menyembuhkan jerawat dan mengontrol kelebihan minyak. Jerawat yang meradang terjadi karena adanya sumbatan pada pori kulit akibat minyak yang berlebih sehingga memberi peluang P.acne, bakteri penyebab jerawat, untuk berkembang biak. Blue Light bekerja dengan cara mengontrol dan mengurangi P.acne, untuk mengurangi produksi minyak yang berlebih sehingga tidak menyumbat pori. Jerawat dapat mengering hanya dalam sekali terapi.'),
(24, 'FT-4', 'Bio Rejuvenation Light Therapy', 160000, 'Bio Rejuvenation Light Therapy adalah therapy dengan Red Light untuk merejuvinasi kulit, mengurangi keriput dan garis halus, serta mengurangi inflamasi. Red Light dapat terpenetrasi dalam samapai ke lapisan dermis kulit, dimana terdapat kolagen dan elastin. Sinar yang terpenetrasi ke dalam kulit akan diserap dan mengakibatkan efek rejuvenasi: membantu regenerasi sel, melancarkan peredaran darah, dan menstimuli produksi kolagen dan elastin.'),
(25, 'FT-4', 'Bio Pigmentation Light Therapy', 160000, 'Bio Pigmentation Light Therapy adalah terapi dengan Green Light yang berguna untuk mencerahkan, dan mengurangi hiperpigmentasi. Green Light dapat terpenetrasi sampai lapisan dasar epidermis, dimana terdapat sel yang meregulasi melanin. Green Light Therapy membantu menekan produksi melanin sehingga dapat memperbaiki dan mencegah hiperpigmentasi dan age-spot.'),
(26, 'FT-4', 'Bio Radiance Light Therapy', 160000, 'Bio Radiance Light Therapy adalah terapi dengan menggunakan Orange Light untuk mengatasi kulit kusam. Orange Light sangat direkomendasikan untuk acara special, seperti pemotretan, wedding, dating ataupun untuk kebutuhan sehari-hari dimana kita menginginkan kulit wajah kita terlihat lebih segar, sehat, dan radiance look.'),
(27, 'FT-4', 'Bio Detoxification Light Therapy', 165000, 'Bio Detoxification Light Therapy adalah terapi dengan Yellow Light yang berguna untuk detoksifikasi, membuang racun dan kotoran dari dalam tubuh. Yellow Light mampu terpenetrasi ke dalam lapisan-lapisan kulit dan menstimulasi kelenjar getah bening. Kelenjar getah bening adalah sistem dalam tubuh yang bertanggung jawab dalam pengeluaran racun untuk keluar dari tubuh (detoksifikasi). Dengan detoksifikasi, kulit akan terlihat lebih segar, cerah, dan lebih siap untuk menerima terpai atau perawatan wajah selanjutnya.'),
(28, 'FT-4', 'Bio Inflamed Acne Light Therapy', 160000, 'Bio-inflamed light therapy adalah terapi dengan Purple Light untuk menyembuhkan jerawat sekaligus mengurangi inflamasi/kemerahan akibat jerawat. Purple Light Therapy menggabungkan Blue Light dan Red Light. Blue light efektif utnuk membunuh kuman P. Acne, bakteri penyebab jerawat, dan mengontrol produksi minyak yang berlebih. Red Light efektif untuk mengurangi kemerahan atau inflamasi akibat jerawat, sehingga mempercepat penyembuhan.'),
(29, 'FT-5', 'Pulsed Oxygen Injection', 250000, 'Pulsed Oxygen Injection merupakan Perawatan wajah dengan infusi oksigen murni, serum vitamin C, dan vacuum massage.  Bermanfaat untuk menyegarkan dan menyehatkan kulit, serta mencegah timbulnya jerawat. Sesai untuk kulit kering, normal, dan berminyak tanpa jerawat.'),
(30, 'FT-5', 'Jet Peel Facial', 300000, 'Jet Peel Facial adalah terapi peremajaan dengan aliran oksigen bertekanan tinggi untuk mengangkat sel kulit mati, memberi nutrisi, melembabkan kulit, dan mencegah timbulnya jerawat. Sesuai untuk semua jenis kulit, juga aman untuk kulit sensitif sekalipun.'),
(31, 'FT-5', 'Microdermabrasion', 300000, 'Microdermabrasion adalah terapi peremajaan dengan mengangkat sel kulit mati secara mekanik untuk menghaluskan, mencerahkan, mengurangi hiperpigmentasi, dan keriput. Kulit akan menjadi lebih halus dan cerah hanya dengan satu kali terapi.'),
(32, 'FT-5', 'Radio Frequency', 335000, 'Radio Frequency adalah terapi untuk mengencangkan kulit, mengurangi keriput, dan garis-garis halus. Menggunakan kombinasi bahan alami dan teknologi radio-frequency untuk menjaga dan meningkatkan produksi kolagen. Kulit akan menjadi lebih kencang hanya dengan hanya satu kali terapi.'),
(33, 'FT-5', 'Mesotherapy Without Needle', 300000, 'Mesotherapy Without Needle adalah terapi peremajaan dengan mesotherapy tanpa jarum, yaitu teknologi terbaru untuk memasukkan serum yang berisi bahan alami, vitamin, dan mineral ke dalam kulit tanpa menggunakan jarum suntik.'),
(34, 'FT-5', 'IPL', 435000, 'IPL adalah perawatan menggunakan alat fototerapi yang mengeluarkan spektrum cahaya berintensitas tinggi, dengan panjang gelombang tertentu, untuk menarget sel-sel kulit secara selektif. Teknologi IPL bekerja hampir sama dengan laser. IPL relatif lebih aman karena menggunakan spektrum cahaya yang luas dari panjang gelombang yang berbeda (polychromatic). Sehingga mampu menarget banyak chromophores.'),
(35, 'FT-5', 'Nano Fractional', 330000, 'Nano Fractional adalah perawatan kombinasi raddio frequency dan fractional yang efektif untuk memperbaiki dan meremajakan kulit yang rusak dan bersinergi menstimulasi pembentukan kolagen serta regenerasi kulit. Perawatan ini dapat mengurangi munculnya keriput, bekas luka, mengecilkan pori-pori, memudarkan garis senyum, dan menciptakan kulit yang lebih halus dan sehat.'),
(36, 'FT-6', 'Honey Facial', 120000, 'Honey Facial merupakan perawatan wajah dengan menggunakan cleanser, scrub, massage cream dan masker dari ramuan dengan madu asli. Perawatan ini sesuai untuk kulit kering, normal dan sensitif. Madu selain mengandung Anti Oxidant yang berasal dari beberapa vitamin dan mineral yang terkandung di dalamanya dapt mencegah penuaan dini, madu juga mengandung Anti Bacteria atau yang disebut dengan Antibiotik yang berfungsi untuk membunuh kuman, menetralkan dan menyembuhkan iritasi/infeksi, mencegah dan menyembuhkan jerawat, serta mengandung zat Progenetic Stonulous yang membantu meningkatkan keaktifan jaringan kulit.'),
(37, 'FT-6', 'Dark Chocolate Facial', 120000, 'Dark Chocolate Facial merupaakan perawatan wajah dengan cleanser, scrub, massage cream, dan masker dari coklat asli. Sesuai untuk kulit kering, normal, dan berminyak tanpa jerawat. Cocoa Butter merupakan unsur atau kandungan terkuat yang terdapat dalam coklat yang berfungsi melapisi dan melindungi kulit dari kotoran, polusi, sinar matahari, menyerap kotoran yang menempel pada kulit serta melembutkan dan menghaluskan kulit. Kemudian, selain memiliki kandungan Antioxidant yang cukup tinggi yang disebut dengan ORAC (Oxygen Radical Absorption Capacity) yang berperan sebagai penghambat terjadinya oksidasi oleh udara bebas, coklat juga kaya akan kandungan seperti lemak jenuh dan tak jenuh, Asam Amino, Protein, Karbohidrat, Kalium, Magnesium, serta berbagai macam vitamin A, B, B2, B6, B12, C dan E yang sangat baik untuk kesehatan kulit.'),
(38, 'FT-6', 'Romantic Radiance Rose Facial', 125000, 'Romantic Radiance Rose Facial merupakan perawatan wajah eksklusif yang menggunakan rangkaian produk berbahan dasar mawar asli, mulai dari cleanser, scrub, massage cream, hingga masker. Perawatan ini diformulasikan khusus untuk semua jenis kulit, termasuk kulit sensitif yang mudah iritasi. Kandungan utama dari bunga mawar memiliki manfaat luar biasa dalam menenangkan kulit, mengurangi kemerahan, serta menjaga kelembapan alami kulit wajah.'),
(39, 'FT-6', 'Kurma Facial', 120000, 'Kurma Facial merupakan perawatan wajah yang menggunakan rangkaian produk berbahan dasar buah kurma, meliputi cleanser, scrub, massage cream, dan masker alami. Perawatan ini sangat cocok untuk kulit kering, kusam, dan lelah, karena kurma dikenal kaya akan nutrisi penting yang mampu menghidrasi sekaligus menutrisi kulit secara mendalam.'),
(40, 'FT-7', '02 Bubble Mask Facial Avocado', 180000, '02 Bubble Mask Facial Avocado merupakan perawatan wajah yang memadukan masker gelembung oksigen (O Bubble Mask) dengan 100% buah alpukat segar yang dicacah asli. Perawatan ini dirancang untuk memberikan sensasi menyegarkan sekaligus membantu proses detoksifikasi kulit melalui teknologi oksigen aktif yang menghasilkan gelembung-gelembung mikro pada permukaan kulit.'),
(41, 'FT-7', '02 Bubble Mask Facial Strawberry', 180000, '02 Bubble Mask Facial Strawberry merupakan perawatan wajah yang memadukan keunggulan masker gelembung oksigen (O Bubble Mask) dengan 100% buah stroberi segar yang dicacah asli. Perawatan ini menawarkan manfaat detoksifikasi kulit secara menyeluruh melalui gelembung mikro aktif yang bekerja membersihkan pori-pori secara mendalam, sekaligus menyuplai oksigen untuk merevitalisasi kulit.'),
(42, 'FT-7', '02 Bubble Mask Facial Yam Bean', 180000, '02 Bubble Mask Facial Yam Bean adalah perawatan wajah yang menggabungkan teknologi masker gelembung oksigen (O Bubble Mask) dengan 100% buah bengkuang (yam bean) segar yang dicacah asli. Masker oksigen ini menghasilkan gelembung mikro aktif yang membantu membersihkan pori-pori secara menyeluruh, mengangkat kotoran, serta meningkatkan asupan oksigen ke dalam kulit untuk membantu proses regenerasi sel.'),
(43, 'FT-8', 'Super Bio', 260000, 'Super Bio adalah terapi intensif yang menggabungkan kekuatan Super Blue Light dan Bio Inflamed Light Therapy (Purple Light) untuk mengatasi jerawat secara menyeluruh. Super Blue Light bekerja aktif membunuh bakteri P.acne penyebab jerawat, mengontrol produksi minyak berlebih, serta membersihkan pori-pori yang tersumbat. Sementara Bio Inflamed Light, kombinasi dari Blue dan Red Light, membantu mengurangi kemerahan, peradangan, dan mempercepat proses penyembuhan kulit. Perpaduan kedua terapi ini memberikan hasil yang efektif tanpa rasa sakit, menjadikan kulit lebih bersih, tenang, dan terbebas dari jerawat aktif maupun inflamasi.'),
(44, 'FT-8', 'Silver Bio', 290000, 'Silver Bio adalah perawatan wajah yang memadukan manfaat Silver Facial dengan teknologi Bio Pigmentation Light Therapy (Green Light) untuk mencerahkan kulit secara menyeluruh. Silver Facial membantu membersihkan, menenangkan, dan meremajakan kulit dengan kandungan antibakteri alami dari silver yang juga efektif merawat kulit sensitif. Sementara Green Light dalam Bio Pigmentation menembus hingga lapisan dasar epidermis untuk menghambat produksi melanin, sehingga mampu mengurangi flek hitam, hiperpigmentasi, dan meratakan warna kulit. Kombinasi keduanya memberikan hasil kulit yang tampak lebih cerah, sehat, dan bercahaya alami.'),
(45, 'FT-8', 'Gold Bio', 285000, 'Gold Bio adalah perawatan wajah eksklusif yang menggabungkan keunggulan Gold Facial dengan teknologi Bio Radiance Light Therapy (Orange Light) untuk menghasilkan kulit yang tampak cerah, segar, dan bercahaya alami. Gold Facial dikenal dengan kandungan emas aktif yang membantu menstimulasi regenerasi sel kulit, meningkatkan elastisitas, serta memperlambat tanda-tanda penuaan dini. Sementara Orange Light dalam Bio Radiance menembus lapisan kulit untuk memberikan efek revitalisasi instanmeningkatkan sirkulasi darah dan menciptakan tampilan kulit yang lebih sehat dan glowing. Kombinasi perawatan ini sangat ideal untuk momen-momen spesial atau perawatan rutin bagi Anda yang menginginkan kulit tampak mewah, segar, dan bercahaya setiap saat.'),
(46, 'FT-8', 'Colagen Bio', 275000, 'Collagen Bio adalah perawatan wajah yang mengombinasikan manfaat Collagen Facial dengan teknologi Bio Rejuvenation Light Therapy (Red Light) untuk membantu mengurangi tanda-tanda penuaan dan menjaga elastisitas kulit. Collagen Facial bekerja dengan memberikan nutrisi langsung ke dalam lapisan kulit untuk menstimulasi produksi kolagen alami, mengencangkan kulit, dan mengurangi garis halus. Sementara Red Light dalam Bio Rejuvenation menembus hingga ke lapisan dermis untuk mempercepat regenerasi sel, melancarkan sirkulasi darah, serta meningkatkan produksi kolagen dan elastin. Sinergi dari kedua perawatan ini menjadikan kulit tampak lebih kencang, halus, dan awet muda dengan kilau sehat alami.'),
(47, 'FT-9', 'Bio Rejuvenation Light Therapy', 160000, 'Bio Rejuvenation Light Therapy adalah therapy dengan Red Light untuk merejuvinasi kulit, mengurangi keriput dan garis halus, serta mengurangi inflamasi. Red Light dapat terpenetrasi dalam samapai ke lapisan dermis kulit, dimana terdapat kolagen dan elastin. Sinar yang terpenetrasi ke dalam kulit akan diserap dan mengakibatkan efek rejuvenasi: membantu regenerasi sel, melancarkan peredaran darah, dan menstimuli produksi kolagen dan elastin.'),
(48, 'FT-9', 'Collagen Facial', 180000, 'Collagen Facial merupakan perawatan muka dengan kombinasi mikrodermabrasi ringan, infusi serum kolagen dan masker kolagen. Dengan bantuan ultrasound molekul kolagen dapat terpenetrasi ke dalam kulit untuk mengembalikan keelastisan dan kekencengan kulit, menaikkan kelembaban kulit, dan menjaga keutuhan kolagen alami kulit. Sesuai untuk kulit normal, kering, dan kusam.'),
(49, 'FT-9', 'Mesotherapy Lifting Facial', 300000, 'Mesotherapy Lifting Facial adalah perawatan wajah dengan teknik mesotherapy non-invasif yang diformulasikan khusus untuk mengencangkan dan mengangkat kontur wajah secara alami. Mengandung campuran peptida, kolagen, dan bahan aktif lainnya yang diserap langsung ke dalam lapisan kulit untuk meningkatkan elastisitas, mengurangi kendur, serta menyamarkan garis halus dan kerutan. Perawatan ini merangsang produksi kolagen dan memperkuat struktur kulit dari dalam, sehingga wajah tampak lebih kencang, terangkat, dan tampak muda. Ideal untuk Anda yang ingin mengatasi tanda-tanda penuaan dan mendapatkan tampilan wajah yang lebih tegas dan segar.'),
(50, 'FT-10', 'Gold Facial', 205000, 'gold facial adalah Perawatan wajah yang mengkombinasikan mikrodermabrasi ringan, ultrasound, dan masker emas murni. Bermanfaat untuk menghaluskan, melembabkan, dan mencerahkan kulit. Sesuai untuk kulit kering, normal, dan berminyak tanpa jerawat.'),
(51, 'FT-10', 'Romantic Radiance Rose Facial', 125000, 'Romantic Radiance Rose Facial merupakan perawatan wajah eksklusif yang menggunakan rangkaian produk berbahan dasar mawar asli, mulai dari cleanser, scrub, massage cream, hingga masker. Perawatan ini diformulasikan khusus untuk semua jenis kulit, termasuk kulit sensitif yang mudah iritasi. Kandungan utama dari bunga mawar memiliki manfaat luar biasa dalam menenangkan kulit, mengurangi kemerahan, serta menjaga kelembapan alami kulit wajah.'),
(52, 'FT-10', 'Silver Facial', 205000, 'Silver Facial merupakan perawatan wajah yang mengkombinasikan mikrodermabrasi ringan dengan ultrasound dan masker silver murni. Silver murni bekerja dengan menyerang langsung pada melanin kulit sehingga menjadikan kulit lebih putih serta dapat menyamarkan/mengurangi pigmentasi. Silver juga kaya akan mineral yang sangat baik dalam pemulihan kulit yang dehidrasi. Sesuai untuk kulit kering, dehidrasi, normal, berminyak, kusam, pigmentasi.'),
(53, 'FT-10', 'Bio Pigmentation Light Therapy', 160000, 'Bio Pigmentation Light Therapy adalah terapi dengan Green Light yang berguna untuk mencerahkan, dan mengurangi hiperpigmentasi. Green Light dapat terpenetrasi sampai lapisan dasar epidermis, dimana terdapat sel yang meregulasi melanin. Green Light Therapy membantu menekan produksi melanin sehingga dapat memperbaiki dan mencegah hiperpigmentasi dan age-spot.'),
(54, 'FT-10', 'Mesotherapy Brightening Facial', 300000, 'Mesotherapy Brightening Facial adalah perawatan wajah dengan teknik mesotherapy non-invasif yang diformulasikan untuk mencerahkan kulit secara menyeluruh. Diperkaya dengan vitamin C, antioksidan, dan bahan aktif pencerah yang diserap langsung ke dalam lapisan kulit untuk membantu mengurangi flek hitam, meratakan warna kulit, dan mengembalikan kilau alami wajah. Perawatan ini juga membantu meningkatkan metabolisme sel kulit, menjadikan kulit tampak lebih cerah, segar, dan bercahaya dari dalam. Cocok untuk semua jenis kulit yang kusam, tidak merata, atau mengalami hiperpigmentasi ringan hingga sedang.'),
(55, 'FT-10', 'Pulsed Oxygen Injection', 250000, 'Pulsed Oxygen Injection merupakan Perawatan wajah dengan infusi oksigen murni, serum vitamin C, dan vacuum massage.  Bermanfaat untuk menyegarkan dan menyehatkan kulit, serta mencegah timbulnya jerawat. Sesai untuk kulit kering, normal, dan berminyak tanpa jerawat.'),
(56, 'FT-10', 'Microderm Rev Diamond', 230000, 'Microderm Rev Diamond adalah perawatan eksfoliasi wajah yang menggunakan teknologi diamond tip microdermabrasion untuk mengangkat sel kulit mati secara menyeluruh dan merangsang regenerasi kulit baru. Dengan proses yang lembut namun efektif, perawatan ini membantu menghaluskan tekstur kulit, menyamarkan bekas jerawat, garis halus, dan pori-pori besar. Microdermabrasion dengan ujung berlian memberikan hasil yang lebih presisi dan higienis, menjadikan kulit tampak lebih cerah, bersih, dan halus seketika. Cocok untuk semua jenis kulit yang ingin tampil segar, sehat, dan bercahaya.'),
(57, 'FT-10', 'Microderm Rev Face Up', 305000, 'Microderm Rev Face Up adalah perawatan eksfoliasi wajah yang menggabungkan teknologi microdermabrasion dengan teknik pengangkatan (lifting) untuk hasil kulit yang lebih halus, cerah, dan tampak kencang. Perawatan ini bekerja dengan mengangkat sel kulit mati menggunakan alat khusus, sekaligus menstimulasi sirkulasi darah dan produksi kolagen untuk meningkatkan elastisitas kulit. Efektif untuk menyamarkan garis halus, pori-pori besar, serta membuat kontur wajah terlihat lebih tegas. Cocok untuk Anda yang menginginkan kulit bersih, segar, dan tampak lebih muda dalam waktu singkat.'),
(58, 'FT-10', 'Jet Peel Facial', 300000, 'Jet Peel Facial adalah terapi peremajaan dengan aliran oksigen bertekanan tinggi untuk mengangkat sel kulit mati, memberi nutrisi, melembabkan kulit, dan mencegah timbulnya jerawat. Sesuai untuk semua jenis kulit, juga aman untuk kulit sensitif sekalipun.'),
(59, 'FT-10', 'Bio Radiance Light Therapy', 160000, 'Bio Radiance Light Therapy adalah terapi dengan menggunakan Orange Light untuk mengatasi kulit kusam. Orange Light sangat direkomendasikan untuk acara special, seperti pemotretan, wedding, dating ataupun untuk kebutuhan sehari-hari dimana kita menginginkan kulit wajah kita terlihat lebih segar, sehat, dan radiance look.'),
(60, 'HT-1', 'Medical Hair Treatment', 130000, 'Medical Hair Treatment adalah perawatan rambut intensif yang menggabungkan bahan-bahan alami dan aromaterapi dengan teknologi modern untuk mengatasi berbagai permasalahan rambut dan kulit kepala. Perawatan ini dirancang tidak hanya untuk memberikan relaksasi, tetapi juga manfaat terapeutik yang menyeluruh, mulai dari menstimulasi sirkulasi darah, memperkuat akar rambut, hingga mendukung pertumbuhan rambut yang lebih sehat dan kuat. Tersedia berbagai jenis Hair Spa yang dapat disesuaikan dengan kebutuhan dan kondisi kulit kepala, seperti varian Classics yang diformulasikan khusus untuk rambut kering, normal, berminyak, dan berketombe. Dengan pendekatan yang personal dan efektif, Medical Hair Treatment menjadi solusi ideal untuk menjaga kesehatan rambut dan kulit kepala secara alami dan menyeluruh.'),
(61, 'HT-2', 'Strawberry -Yoghurt', 135000, 'Strawberry-Yoghurt adalah varian dari Fruit Medical Hair Treatment yang diformulasikan khusus untuk melembapkan dan menyuburkan rambut secara alami. Ekstrak stroberi kaya akan antioksidan dan vitamin C yang membantu menutrisi kulit kepala serta melindungi rambut dari kerusakan akibat radikal bebas. Dipadukan dengan yoghurt yang mengandung protein dan asam laktat, perawatan ini bekerja efektif untuk menghidrasi batang rambut, mengurangi kekeringan, serta memperkuat akar rambut. Hasilnya, rambut terasa lebih lembut, sehat, dan tampak lebih berkilau. Sangat cocok untuk rambut kering, kusam, atau yang sering terpapar panas dan polusi.'),
(62, 'HT-2', 'Avocado-Green Tea', 135000, 'Avocado-Green Tea adalah varian Fruit Medical Hair Treatment yang dirancang untuk melembapkan dan menyuburkan rambut secara intensif. Alpukat dikenal kaya akan vitamin E, lemak sehat, dan nutrisi esensial yang membantu memperbaiki rambut kering dan rapuh, serta menjaga kelembapan alami rambut. Sementara ekstrak teh hijau mengandung antioksidan tinggi yang membantu menenangkan kulit kepala, mengurangi kerontokan, dan menstimulasi pertumbuhan rambut baru. Kombinasi bahan alami ini memberikan perawatan menyeluruh untuk rambut yang lebih lembut, kuat, dan bercahaya dari akar hingga ujung.'),
(63, 'HT-2', 'Dark Chocolate', 135000, 'Dark Chocolate adalah varian Fruit Medical Hair Treatment yang diformulasikan khusus untuk memberikan kelembapan mendalam pada rambut kering dan kusam. Ekstrak cokelat hitam kaya akan antioksidan, magnesium, dan asam lemak esensial yang membantu menutrisi batang rambut, memperbaiki kerusakan akibat panas atau bahan kimia, serta mengunci kelembapan alami rambut. Kandungan theobromine dalam cokelat juga diketahui dapat meningkatkan sirkulasi kulit kepala, mendukung kesehatan folikel, dan memberikan efek relaksasi yang menenangkan. Dengan aroma khas cokelat yang hangat dan menenangkan, varian ini menghadirkan pengalaman perawatan rambut yang mewah sekaligus menutrisi, menjadikan rambut lebih lembut, lembap, dan mudah diatur.'),
(64, 'HT-2', 'Jasmine-Aloe Vera', 135000, 'Jasmine-Aloe Vera adalah varian Fruit Medical Hair Treatment yang diformulasikan untuk membantu mencegah kerontokan rambut secara efektif dan alami. Ekstrak bunga melati dikenal memiliki sifat menenangkan dan aromaterapi yang merilekskan, sekaligus membantu memperkuat akar rambut serta merangsang pertumbuhan rambut baru. Sementara lidah buaya (Aloe Vera) kaya akan enzim proteolitik, vitamin A, C, dan E yang bekerja menutrisi kulit kepala, mengurangi peradangan, dan memperbaiki jaringan folikel rambut yang rusak. Kombinasi kedua bahan ini memberikan perawatan menyeluruh yang menyeimbangkan kelembapan kulit kepala, memperkuat helai rambut dari akarnya, dan mengurangi kerontokan secara bertahap. Varian ini cocok bagi Anda yang menginginkan rambut lebih tebal, sehat, dan bebas stres.'),
(65, 'HT-2', 'Coconut-Cinnamon', 135000, 'Coconut-Cinnamon adalah varian Fruit Medical Hair Treatment yang diformulasikan untuk memberikan kilau alami dan sehat pada rambut. Minyak kelapa dikenal akan kemampuannya menembus batang rambut secara mendalam, memberikan nutrisi, serta menjaga kelembapan dan elastisitas rambut. Kandungan asam lemak dan vitamin E-nya membantu menghaluskan helaian rambut dan mengurangi kusam. Sementara kayu manis (cinnamon) mengandung senyawa antimikroba dan antioksidan yang membantu merangsang sirkulasi kulit kepala serta memperkuat akar rambut. Perpaduan aroma hangat dan manis dari kayu manis dengan kesegaran kelapa menciptakan pengalaman perawatan yang memanjakan sekaligus efektif dalam membuat rambut tampak lebih berkilau, lembut, dan sehat alami.'),
(66, 'HT-2', 'Lemon-Honey', 135000, 'Lemon-Honey adalah varian Fruit Medical Hair Treatment yang diformulasikan khusus untuk mengontrol kelebihan minyak pada kulit kepala tanpa menghilangkan kelembapan alaminya. Ekstrak lemon kaya akan vitamin C dan asam sitrat yang efektif membersihkan kulit kepala dari minyak berlebih, kotoran, dan penumpukan produk, sekaligus menyegarkan rambut secara menyeluruh. Sementara madu dikenal sebagai humektan alami yang membantu menjaga kelembapan rambut, menutrisi akar, dan memberikan kilau sehat tanpa membuat rambut lepek. Kombinasi segar dan manis dari lemon dan madu memberikan sensasi bersih, ringan, dan harum alami, menjadikannya pilihan tepat untuk Anda yang menginginkan rambut segar, bebas minyak, dan tetap ternutrisi dengan baik.'),
(67, 'HT-3', 'Medical Hair Mask', 130000, 'Medical Hair Mask adalah perawatan intensif untuk rambut dan kulit kepala yang menggunakan kombinasi bahan-bahan alami berkualitas tinggi dan aromaterapi yang menenangkan, serta didukung oleh penggunaan peralatan modern. Perawatan ini dirancang untuk memberikan nutrisi mendalam ke batang rambut dan akar, membantu mengembalikan vitalitas rambut yang rusak akibat paparan panas, polusi, atau bahan kimia. Kandungan alami dalam masker bekerja menyehatkan kulit kepala, mengurangi kekeringan, serta memberikan kelembapan dan kelembutan ekstra pada rambut, menjadikannya lebih halus, mudah diatur, dan tampak berkilau alami. Medical Hair Mask merupakan solusi ideal bagi Anda yang menginginkan perawatan rambut menyeluruh yang tidak hanya merawat, tetapi juga memanjakan indra melalui sensasi relaksasi dari aromaterapi.'),
(68, 'HT-4', 'Waxing', 250000, 'Waxing adalah terapi perawatan kulit yang bertujuan untuk menghilangkan bulu secara efektif di area tubuh seperti tangan, ketiak, maupun kaki. Proses ini tidak hanya membantu menciptakan tampilan kulit yang lebih bersih dan halus, tetapi juga memperlambat pertumbuhan bulu baru dibandingkan dengan metode pencukuran biasa. Waxing tersedia dalam dua varian, yaitu wax roll yang dirancang khusus untuk jenis rambut yang lebih halus dan tipis, memberikan pengalaman yang lebih lembut dan nyaman, serta wax strip yang lebih kuat dan efektif untuk mengangkat rambut yang lebih tebal dan kasar. Dengan teknik yang tepat dan bahan wax berkualitas, perawatan ini tidak hanya memberikan hasil yang tahan lama, tetapi juga membantu mengeksfoliasi kulit, menjadikannya terasa lebih lembut dan cerah. Waxing cocok bagi Anda yang menginginkan solusi praktis dan tahan lama untuk menjaga kebersihan dan penampilan kulit.'),
(69, 'BT-1', 'Meso Body Perut', 385000, 'Meso Body Perut adalah treatment yang diformulasikan khusus untuk membantu mengurangi lemak berlebih dan mengencangkan area perut secara efektif. Menggunakan teknologi mesoterapi non-invasif, treatment ini mengantarkan bahan aktif langsung ke lapisan kulit yang dalam untuk memecah lemak, meningkatkan sirkulasi darah, dan mempercepat metabolisme lokal. Diperkaya dengan ekstrak alami dan senyawa pelangsing seperti kafein dan L-carnitine, Meso Body Perut bekerja menargetkan lemak membandel di sekitar perut, membantu meratakan kontur tubuh, serta mengurangi tampilan perut buncit. Sensasi hangat yang ditimbulkan juga memberikan efek relaksasi sambil meningkatkan efektivitas penyerapan. Cocok untuk Anda yang menginginkan bentuk perut lebih ramping, kencang, dan proporsional secara alami dan aman.'),
(70, 'BT-1', 'Meso Body Paha', 385000, 'Meso Body Paha adalah treatment yang diformulasikan khusus untuk membantu mengurangi lemak berlebih dan mengencangkan area paha secara efektif. Menggunakan teknologi mesoterapi non-invasif, treatment ini mengantarkan bahan aktif langsung ke lapisan kulit yang dalam untuk memecah lemak, memperlancar sirkulasi darah, dan mempercepat pembakaran lemak lokal di area paha. Diperkaya dengan ekstrak alami dan senyawa pelangsing seperti kafein dan L-carnitine, Meso Body Paha bekerja menargetkan penumpukan lemak dan selulit yang umumnya sulit dihilangkan, serta membantu membentuk kontur paha yang lebih ramping dan kencang. Sensasi hangat yang menyertai perawatan juga memberikan efek relaksasi dan meningkatkan penyerapan zat aktif. Cocok untuk Anda yang menginginkan bentuk paha lebih proporsional, halus, dan bebas selulit secara alami dan aman.'),
(71, 'BT-1', 'Meso Body Lengan', 385000, 'Meso Body Lengan adalah treatment yang diformulasikan khusus untuk membantu mengurangi lemak berlebih dan mengencangkan area lengan atas secara efektif. Dengan teknologi mesoterapi non-invasif, bahan aktif diserap langsung ke lapisan kulit terdalam untuk memecah lemak, meningkatkan sirkulasi darah, dan mempercepat metabolisme lokal di area lengan. Kandungan kafein, L-carnitine, serta ekstrak alami lainnya bekerja secara sinergis untuk menargetkan lemak membandel dan mengurangi tampilan lengan yang bergelambir. Treatment ini juga membantu meningkatkan elastisitas kulit sehingga lengan terasa lebih kencang, halus, dan terdefinisi. Sensasi hangat yang dihasilkan memberikan efek relaksasi sekaligus mempercepat proses penyerapan bahan aktif. Ideal untuk Anda yang ingin memiliki lengan yang lebih ramping, kencang, dan proporsional secara aman dan alami.'),
(72, 'BT-1', 'RF Body Multipolar Perut', 400000, 'RF Body Multipolar Perut adalah treatment pelangsing non-invasif yang menggunakan teknologi Radio Frequency (RF) multipolar untuk membantu mengurangi lemak, mengencangkan kulit, dan membentuk area perut secara optimal. Gelombang RF bekerja dengan menghasilkan panas di lapisan kulit terdalam (dermis), yang merangsang produksi kolagen, mempercepat metabolisme sel lemak, dan memperlancar sirkulasi darah di area perut. Proses ini efektif dalam mengurangi tampilan perut buncit, memperbaiki elastisitas kulit, serta mengencangkan jaringan yang kendur tanpa rasa sakit atau downtime. Sensasi hangat selama treatment memberikan kenyamanan sekaligus hasil yang terlihat lebih cepat dan alami. Cocok untuk Anda yang ingin memiliki perut lebih rata, kencang, dan terlihat lebih ramping secara aman dan modern.'),
(73, 'BT-1', 'RF Body Multipolar Paha', 400000, 'RF Body Multipolar Paha adalah treatment pelangsing modern yang dirancang untuk mengurangi lemak dan mengencangkan area paha secara efektif menggunakan teknologi Radio Frequency (RF) multipolar. Gelombang RF bekerja dengan menghangatkan lapisan dalam kulit (dermis) untuk merangsang produksi kolagen, mempercepat metabolisme lemak, dan meningkatkan sirkulasi darah di area paha. Treatment ini membantu mengurangi tampilan selulit, mengencangkan kulit yang kendur, serta membentuk kontur paha agar tampak lebih ramping dan halus. Prosesnya aman, nyaman, tanpa jarum atau downtime, dengan sensasi hangat yang menenangkan. Cocok untuk Anda yang menginginkan paha yang lebih kencang, proporsional, dan bebas selulit secara alami dan non-invasif.'),
(74, 'BT-1', 'RF Body Multipolar Lengan', 400000, 'RF Body Multipolar Lengan adalah treatment pelangsing non-invasif yang diformulasikan khusus untuk membantu mengurangi lemak dan mengencangkan area lengan atas. Dengan teknologi Radio Frequency (RF) multipolar, treatment ini menghantarkan energi panas ke lapisan dalam kulit untuk merangsang produksi kolagen, membakar lemak, dan memperbaiki elastisitas kulit secara alami. Proses ini efektif untuk mengurangi tampilan lengan yang bergelambir, mempertegas kontur lengan, serta menjadikan kulit lebih kencang dan halus. Sensasi hangat yang nyaman selama treatment mendukung penyerapan energi secara optimal tanpa rasa sakit atau downtime. Cocok untuk Anda yang ingin memiliki lengan ramping, kencang, dan terlihat lebih proporsional dengan metode yang aman dan modern.'),
(75, 'BT-1', 'RF Body Monopolar Lengan', 400000, 'RF Body Monopolar Lengan adalah treatment pelangsing dan pengencangan lengan yang menggunakan teknologi Radio Frequency (RF) monopolar, yang mampu menghantarkan panas lebih dalam ke jaringan subkutan untuk hasil yang lebih intensif. Energi panas dari RF monopolar bekerja menembus hingga lapisan lemak terdalam, merangsang pemecahan lemak, mempercepat metabolisme, serta meningkatkan produksi kolagen untuk kulit yang lebih kencang dan elastis. Treatment ini sangat efektif untuk mengurangi lemak berlebih di area lengan atas, mempertegas kontur lengan, dan mengatasi kulit kendur atau bergelambir. Dengan sensasi hangat yang nyaman dan tanpa downtime, RF Body Monopolar Lengan cocok bagi Anda yang menginginkan lengan lebih ramping, kencang, dan terdefinisi secara alami dan aman.'),
(76, 'BT-1', 'RF Body Monopolar Perut', 400000, 'RF Body Monopolar Perut adalah treatment pelangsing intensif yang dirancang untuk membantu mengurangi lemak membandel dan mengencangkan area perut menggunakan teknologi Radio Frequency (RF) monopolar. Teknologi ini mampu menghantarkan panas hingga ke lapisan lemak terdalam, memecah jaringan lemak, meningkatkan sirkulasi darah, dan merangsang produksi kolagen secara optimal. Hasilnya, kulit perut menjadi lebih kencang, elastis, dan tampilan perut buncit berkurang secara bertahap. RF monopolar juga dikenal efektif untuk memperbaiki tekstur kulit dan mengurangi tampilan stretch mark. Dengan sensasi hangat yang nyaman tanpa rasa sakit atau downtime, treatment ini cocok bagi Anda yang menginginkan bentuk perut yang lebih ramping, rata, dan proporsional secara alami dan aman.'),
(77, 'BT-1', 'RF Body Monopolar Paha', 400000, 'RF Body Monopolar Paha adalah treatment pelangsing dan pengencangan yang dirancang khusus untuk mengatasi penumpukan lemak dan kulit kendur di area paha. Menggunakan teknologi Radio Frequency (RF) monopolar, treatment ini menghantarkan panas ke lapisan lemak terdalam untuk memecah lemak, mempercepat metabolisme sel, dan merangsang produksi kolagen secara maksimal. Proses ini efektif mengurangi selulit, mengencangkan kulit, serta membentuk kontur paha agar tampak lebih ramping dan halus. Sensasi hangat selama treatment memberikan efek relaksasi tanpa rasa sakit atau downtime, menjadikannya pilihan ideal bagi Anda yang menginginkan paha lebih kencang, terdefinisi, dan proporsional secara alami dan aman.'),
(78, 'BT-2', 'Body Firming', 400000, 'Body Firming adalah perawatan khusus yang menggunakan teknologi Radio Frequency (RF) untuk membantu mengencangkan kulit dan mengurangi tampilan stretch mark di berbagai area tubuh seperti perut, paha, lengan, dan pinggang. Energi panas dari RF bekerja menembus hingga ke lapisan dermis untuk merangsang produksi kolagen dan elastin  dua komponen penting yang menjaga kekencangan dan elastisitas kulit. Proses ini tidak hanya membantu mengencangkan kulit yang kendur akibat penurunan berat badan atau penuaan, tetapi juga efektif dalam memperbaiki tekstur kulit yang tidak rata, termasuk bekas stretch mark.'),
(79, 'BR-1', 'Avocado Deep Moisturizing Treatment', 250000, 'Avocado Deep Moisturizing Treatment adalah perawatan badan yang diperkaya dengan ekstrak alpukat yang kaya akan vitamin E dan lemak sehat, varian ini memberikan hidrasi intensif sekaligus membantu memperbaiki tekstur kulit yang kasar dan kering. Cocok untuk kulit sangat kering dan membutuhkan kelembapan mendalam.'),
(80, 'BR-1', 'Milky Mild Banana Body Treatment', 250000, 'Milky Mild Banana Body Treatment adalah perawatan badan yang mengandung ekstrak pisang dan susu yang menutrisi dan melembutkan kulit secara alami. Pisang kaya akan vitamin A, B, dan C yang membantu menjaga elastisitas kulit, sementara susu memberikan efek menenangkan dan melembapkan secara menyeluruh.'),
(81, 'BR-1', 'Honey Body Treatment', 250000, 'Honey Body Treatment adalah perawatan badan yang mengandalkan manfaat alami madu sebagai humektan yang menjaga kelembapan kulit, sekaligus memiliki sifat antibakteri dan antioksidan. Perawatan ini ideal untuk kulit yang kering dan sensitif, menjadikannya lebih sehat dan glowing alami.'),
(82, 'BR-1', 'Silky Milk Body Treatment', 250000, 'Silky Milk Body Treatment adalah perawatan badan yang mengandung susu murni yang membantu mencerahkan, menghaluskan, dan melembapkan kulit secara merata. Varian ini memberikan sensasi lembut seperti sutra, menjadikan kulit terasa lebih halus, segar, dan cerah setelah perawatan.'),
(83, 'BR-2', 'Strawberry Yoghurt Body Treatment', 250000, 'Strawberry Yoghurt Body Treatment adalah perawatan badan yang menghadirkan perpaduan ekstrak stroberi segar dan yoghurt kaya probiotik yang membantu menutrisi kulit, memberikan kelembapan alami, serta memperbaiki tekstur kulit yang kasar. Aroma manis stroberi menambah sensasi menyegarkan dan menenangkan.'),
(84, 'BR-2', 'Peach Dazzling Body Treatment', 250000, 'Peach Dazzling Body Treatment adalah perawatan badan yang diperkaya dengan ekstrak buah persik yang kaya vitamin dan antioksidan, varian ini membantu mencerahkan kulit dan memberikan kilau sehat alami. Aroma buah persik yang lembut dan segar menciptakan pengalaman spa yang menyenangkan dan memanjakan.'),
(85, 'BR-2', 'Floral Lavender Body Treatment', 250000, 'Floral Lavender Body Treatment adalah perawatan badan menggunakan ekstrak lavender yang terkenal dengan efek relaksasi dan menenangkan, treatment ini ideal untuk meredakan ketegangan dan membantu tidur lebih nyenyak. Aroma floral lavender juga memberikan kesan menenangkan dan menyegarkan sepanjang hari.'),
(86, 'BR-3', 'Green Tea Body Treatment', 260000, 'Green Tea Body Treatment adalah perawatan badan yang mengandung ekstrak teh hijau kaya antioksidan yang efektif menangkal radikal bebas, mengurangi peradangan, dan menenangkan kulit. Treatment ini juga membantu memperbaiki tekstur kulit serta menjaga kelembapan alami.'),
(87, 'BR-3', 'Dark Chocolate Body Treatment', 260000, 'Dark Chocolate Body Treatment adalah perawatan badan yang diperkaya dengan cokelat hitam yang tinggi kandungan flavonoid dan antioksidan, varian ini membantu memperlambat penuaan, meningkatkan elastisitas kulit, dan memberikan sensasi hangat yang menenangkan.'),
(88, 'BR-3', 'Black Coffee-Cinnamon Body Treatment', 260000, 'Black Coffee-Cinnamon Body Treatment adalah perawatan badan yang menggunakan kombinasi kopi hitam dan kayu manis yang kaya antioksidan dan kafein ini merangsang sirkulasi darah, membantu mengurangi tampilan selulit, serta memberikan efek menyegarkan dan hangat pada kulit.'),
(89, 'BR-3', 'Dead Sea Body Treatment', 260000, 'Dead Sea Body Treatment adalah perawatan badan yang menggunakan mineral alami dari Laut Mati yang kaya magnesium, kalsium, dan potasium, treatment ini membantu detoksifikasi kulit, mempercepat regenerasi sel, dan mengembalikan kelembapan serta keseimbangan kulit secara optimal.'),
(90, 'BR-4', 'Yam Bean Silky Whitening Body Treatment', 285000, 'Yam Bean Silky Whitening Body Treatment adalah perawatan badan yang mengandung ekstrak bengkuang (yam bean) yang kaya vitamin C dan antioksidan, varian ini efektif mencerahkan kulit, menyamarkan noda hitam, dan memberikan kelembapan intensif. Hasilnya kulit terasa halus, lembut, dan bercahaya alami.'),
(91, 'BR-4', 'Soya Bean and Potato Body Treatment', 285000, 'Soya Bean and Potato Body Treatment adalah perawatan badan yang menggunakan kombinasi ekstrak kedelai dan kentang membantu menghambat produksi melanin penyebab kulit gelap, sekaligus menutrisi kulit agar lebih cerah dan lembap. Treatment ini juga membantu memperbaiki tekstur kulit yang tidak merata.'),
(92, 'BR-4', 'Glamorous Gold Body Treatment', 300000, 'Glamorous Gold Body Treatment adalah perawatan badan yang menggunakan partikel emas murni yang mampu menstimulasi regenerasi sel dan meningkatkan elastisitas kulit. Varian ini memberikan efek kilau mewah sekaligus membantu mencerahkan dan mengencangkan kulit tubuh secara menyeluruh.');

